mapper.select:
SELECT Driver.id AS Driver_id, Driver.createtime AS Driver_createtime, Driver.username AS Driver_username, Driver.usertype AS Driver_usertype, Driver.logintime AS Driver_logintime, Od.id AS Order_id, Od.unit_price AS Order_unit_price, (unit_price*distance+Od.passcost+Od.parkcost+Od.trafcost+Od.extras) AS Order_total, Od.passcost AS Order_passcost, Od.parkcost AS Order_parkcost, Od.trafcost AS Order_trafcost, Od.extras AS Order_extras, Od.distance AS Order_distance, Od.starttime AS Order_starttime, Od.endtime AS Order_endtime, Od.remark AS Order_remark, Od.order_status AS Order_status, Od.createtime AS Order_createtime, Od.updatetime AS Order_updatetime, Od.dest AS Order_dest, Od.reason AS Order_reason, Od.updatetime AS Order_updatetime, DEPART.id AS Department_id, DEPART.name AS Department_name, STATUS.name AS Status_name, CAR.car_number AS Car_name, CAR.id AS Car_id FROM serviceCar.sc_order Od LEFT JOIN (SELECT * FROM sc_user WHERE usertype = 1) Driver ON Driver.id = Od.driver_id LEFT JOIN serviceCar.sc_department DEPART ON DEPART.id = Od.department_id LEFT JOIN serviceCar.sc_orderStatus STATUS ON Od.order_status = STATUS.id LEFT JOIN serviceCar.sc_car CAR ON Od.car_id = CAR.id
Od.id = #{OrderId}
limit 1
